		if ($this->isColumnModified(ImportMappingPeer::COLUMN)) $criteria->add(ImportMappingPeer::COLUMN, $this->column);
		if ($this->isColumnModified(ImportMappingPeer::IMPORT_FILE_TYPE)) $criteria->add(ImportMappingPeer::IMPORT_FILE_TYPE, $this->import_file_type);
		if ($this->isColumnModified(ImportMappingPeer::MAPPING)) $criteria->add(ImportMappingPeer::MAPPING, $this->mapping);
		if ($this->isColumnModified(ImportMappingPeer::RATING_FIELD_ID)) $criteria->add(ImportMappingPeer::RATING_FIELD_ID, $this->rating_field_id);
		if ($this->isColumnModified(ImportMappingPeer::QUESTION_RATING)) $criteria->add(ImportMappingPeer::QUESTION_RATING, $this->question_rating);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
