  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseImportMapping:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseImportMapping::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


